import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../CSS Sheets/ResultsPage.css';

interface ResultData {
    card: string;
    id: string;
    card_count: string;
    Ungraded: string;
    'Grade 1': string;
    'Grade 2': string;
    'Grade 3': string;
    'Grade 4': string;
    'Grade 5': string;
    'Grade 6': string;
    'Grade 7': string;
    'Grade 8': string;
    'Grade 9': string;
    'Grade 9.5': string;
    'SGC 10': string;
    'CGC 10': string;
    'PSA 10': string;
    'BGS 10': string;
    'BGS 10 Black': string;
    'CGC 10 Pristine': string;
    final_link: string;
}

const ResultsPage: React.FC = () => {
    const [results, setResults] = useState<ResultData[]>([]);
    const [error, setError] = useState<string | null>(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchResults = async () => {
            try {
                const response = await fetch('http://localhost:8000/results');
                if (!response.ok) {
                    throw new Error('Failed to fetch results');
                }
                const data = await response.json();
                // Extract the results from the response
                const formattedResults = Object.keys(data.results.card).map(key => ({
                    card: data.results.card[key],
                    id: data.results.id[key],
                    card_count: data.results.card_count[key],
                    Ungraded: data.results.Ungraded[key],
                    'Grade 1': data.results['Grade 1'][key],
                    'Grade 2': data.results['Grade 2'][key],
                    'Grade 3': data.results['Grade 3'][key],
                    'Grade 4': data.results['Grade 4'][key],
                    'Grade 5': data.results['Grade 5'][key],
                    'Grade 6': data.results['Grade 6'][key],
                    'Grade 7': data.results['Grade 7'][key],
                    'Grade 8': data.results['Grade 8'][key],
                    'Grade 9': data.results['Grade 9'][key],
                    'Grade 9.5': data.results['Grade 9.5'][key],
                    'SGC 10': data.results['SGC 10'][key],
                    'CGC 10': data.results['CGC 10'][key],
                    'PSA 10': data.results['PSA 10'][key],
                    'BGS 10': data.results['BGS 10'][key],
                    'BGS 10 Black': data.results['BGS 10 Black'][key],
                    'CGC 10 Pristine': data.results['CGC 10 Pristine'][key],
                    final_link: data.results.final_link[key],
                }));
                setResults(formattedResults);
            } catch (err) {
                setError((err as Error)?.message || 'An unknown error occurred');
                console.error('Error fetching results:', err);
            }
        };

        fetchResults();
    }, []);

    const convertToCSV = (data: ResultData[]) => {
        const header = [
            'Card',
            'ID',
            'Card Count',
            'Ungraded',
            'Grade 1',
            'Grade 2',
            'Grade 3',
            'Grade 4',
            'Grade 5',
            'Grade 6',
            'Grade 7',
            'Grade 8',
            'Grade 9',
            'Grade 9.5',
            'SGC 10',
            'CGC 10',
            'PSA 10',
            'BGS 10',
            'BGS 10 Black',
            'CGC 10 Pristine',
            'Final Link',
            'Card Count',
        ].join(',');

        const rows = data.map(item => [
            item.card,
            item.id,
            item.card_count,
            item.Ungraded,
            item['Grade 1'],
            item['Grade 2'],
            item['Grade 3'],
            item['Grade 4'],
            item['Grade 5'],
            item['Grade 6'],
            item['Grade 7'],
            item['Grade 8'],
            item['Grade 9'],
            item['Grade 9.5'],
            item['SGC 10'],
            item['CGC 10'],
            item['PSA 10'],
            item['BGS 10'],
            item['BGS 10 Black'],
            item['CGC 10 Pristine'],
            item.final_link,
            item.card_count,
        ].join(',')).join('\n');

        return `${header}\n${rows}`;
    };

    const downloadCSV = () => {
        const csvData = convertToCSV(results);
        const blob = new Blob([csvData], { type: 'text/csv;charset=utf-8;' });
        const link = document.createElement('a');
        link.href = URL.createObjectURL(blob);
        link.setAttribute('download', 'results.csv');
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    const goHome = () => {
        navigate('/'); // Navigate to the root path
    };

    // Function to calculate totals for each grade category
    const calculateTotals = (results: ResultData[]) => {
        const initialTotals = {
            card_count: 0,
            Ungraded: 0,
            'Grade 1': 0,
            'Grade 2': 0,
            'Grade 3': 0,
            'Grade 4': 0,
            'Grade 5': 0,
            'Grade 6': 0,
            'Grade 7': 0,
            'Grade 8': 0,
            'Grade 9': 0,
            'Grade 9.5': 0,
            'SGC 10': 0,
            'CGC 10': 0,
            'PSA 10': 0,
            'BGS 10': 0,
            'BGS 10 Black': 0,
            'CGC 10 Pristine': 0,
        };

        return results.reduce((totals, item) => {
            totals.card_count += parseInt(item.card_count) || 0;
            totals.Ungraded += parseInt(item.Ungraded) || 0;
            totals['Grade 1'] += parseInt(item['Grade 1']) || 0;
            totals['Grade 2'] += parseInt(item['Grade 2']) || 0;
            totals['Grade 3'] += parseInt(item['Grade 3']) || 0;
            totals['Grade 4'] += parseInt(item['Grade 4']) || 0;
            totals['Grade 5'] += parseInt(item['Grade 5']) || 0;
            totals['Grade 6'] += parseInt(item['Grade 6']) || 0;
            totals['Grade 7'] += parseInt(item['Grade 7']) || 0;
            totals['Grade 8'] += parseInt(item['Grade 8']) || 0;
            totals['Grade 9'] += parseInt(item['Grade 9']) || 0;
            totals['Grade 9.5'] += parseInt(item['Grade 9.5']) || 0;
            totals['SGC 10'] += parseInt(item['SGC 10']) || 0;
            totals['CGC 10'] += parseInt(item['CGC 10']) || 0;
            totals['PSA 10'] += parseInt(item['PSA 10']) || 0;
            totals['BGS 10'] += parseInt(item['BGS 10']) || 0;
            totals['BGS 10 Black'] += parseInt(item['BGS 10 Black']) || 0;
            totals['CGC 10 Pristine'] += parseInt(item['CGC 10 Pristine']) || 0;
            return totals;
        }, initialTotals);
    };

    const totalsRow = calculateTotals(results);

    // Create rows without using map
    const renderRows = () => {
        const rows = [];
        for (let i = 0; i < results.length; i++) {
            const result = results[i];
            rows.push(
                <tr key={i}>
                    <td>{result.card}</td>
                    <td>{result.id}</td>
                    <td>{result.card_count}</td>
                    <td>{result.Ungraded}</td>
                    <td>{result['Grade 1']}</td>
                    <td>{result['Grade 2']}</td>
                    <td>{result['Grade 3']}</td>
                    <td>{result['Grade 4']}</td>
                    <td>{result['Grade 5']}</td>
                    <td>{result['Grade 6']}</td>
                    <td>{result['Grade 7']}</td>
                    <td>{result['Grade 8']}</td>
                    <td>{result['Grade 9']}</td>
                    <td>{result['Grade 9.5']}</td>
                    <td>{result['SGC 10']}</td>
                    <td>{result['CGC 10']}</td>
                    <td>{result['PSA 10']}</td>
                    <td>{result['BGS 10']}</td>
                    <td>{result['BGS 10 Black']}</td>
                    <td>{result['CGC 10 Pristine']}</td>
                    <td>{result.final_link}</td>
                </tr>
            );
        }
        return rows;
    };

    return (
        <div>
            <h1>Results</h1>
            {error && <p>Error: {error}</p>}
            <button onClick={downloadCSV} style={{ marginBottom: '20px' }} className="download-button">
                Download CSV
            </button>
            <button onClick={goHome} style={{ marginBottom: '20px', marginLeft: '10px' }}>
                Return to Input Page
            </button>
            <table className="results-table">
                <thead>
                    <tr>
                        <th>Card</th>
                        <th>ID</th>
                        <th>Card Count</th>
                        <th>Ungraded</th>
                        <th>Grade 1</th>
                        <th>Grade 2</th>
                        <th>Grade 3</th>
                        <th>Grade 4</th>
                        <th>Grade 5</th>
                        <th>Grade 6</th>
                        <th>Grade 7</th>
                        <th>Grade 8</th>
                        <th>Grade 9</th>
                        <th>Grade 9.5</th>
                        <th>SGC 10</th>
                        <th>CGC 10</th>
                        <th>PSA 10</th>
                        <th>BGS 10</th>
                        <th>BGS 10 Black</th>
                        <th>CGC 10 Pristine</th>
                        <th>Final Link</th>
                    </tr>
                </thead>
                <tbody>
                    {renderRows()}
                    <tr>
                        <td><strong>Totals</strong></td>
                        <td></td>
                        <td>{totalsRow.card_count}</td>
                        <td>{totalsRow.Ungraded}</td>
                        <td>{totalsRow['Grade 1']}</td>
                        <td>{totalsRow['Grade 2']}</td>
                        <td>{totalsRow['Grade 3']}</td>
                        <td>{totalsRow['Grade 4']}</td>
                        <td>{totalsRow['Grade 5']}</td>
                        <td>{totalsRow['Grade 6']}</td>
                        <td>{totalsRow['Grade 7']}</td>
                        <td>{totalsRow['Grade 8']}</td>
                        <td>{totalsRow['Grade 9']}</td>
                        <td>{totalsRow['Grade 9.5']}</td>
                        <td>{totalsRow['SGC 10']}</td>
                        <td>{totalsRow['CGC 10']}</td>
                        <td>{totalsRow['PSA 10']}</td>
                        <td>{totalsRow['BGS 10']}</td>
                        <td>{totalsRow['BGS 10 Black']}</td>
                        <td>{totalsRow['CGC 10 Pristine']}</td>
                    </tr>
                </tbody>
            </table>
        </div>
    );
};

export default ResultsPage;
